// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract CertificateStore {
    address private owner;

    event FileCertified(
        address indexed certifier,
        address indexed student,
        string ipfsHash,
        uint timestamp
    );

    struct Certificate {
        string name;
        string organization;
        string details;
        address certifier;
        address student;
        string ipfsHash;
        uint timestamp;
    }

    mapping(address => bool) private authorizedCertifiers; // Authorized certifiers
    mapping(address => Certificate[]) private certificatesByStudent; // List of certificates per student
    mapping(string => Certificate) private certificateByHash; // Store certificates by IPFS hash

    modifier onlyOwner() {
        require(msg.sender == owner, "Only contract owner can call this function.");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function addAuthorizedCertifier(address certifier) public onlyOwner {
        authorizedCertifiers[certifier] = true; // Owner can authorize certifiers
    }

    function removeAuthorizedCertifier(address certifier) public onlyOwner {
        authorizedCertifiers[certifier] = false; // Remove certifiers
    }

    function certifyFile(
        string memory name,
        string memory organization,
        string memory details,
        address student,
        string memory ipfsHash
    ) public payable {
        require(authorizedCertifiers[msg.sender], "Caller is not an authorized certifier");
        require(student != address(0), "Invalid student address");

        Certificate memory newCertificate = Certificate(
            name,
            organization,
            details,
            msg.sender,
            student,
            ipfsHash,
            block.timestamp
        );

        certificatesByStudent[student].push(newCertificate);
        certificateByHash[ipfsHash] = newCertificate; // Store in mapping for faster lookup

        emit FileCertified(msg.sender, student, ipfsHash, block.timestamp);
    }

    // Fetch a single certificate by its index (prevents gas limit issues)
    function getCertificateByIndex(uint index) public view returns (Certificate memory) {
        require(index < certificatesByStudent[msg.sender].length, "Invalid index");
        return certificatesByStudent[msg.sender][index];
    }

    // Fetch certificate by IPFS hash in O(1) time
    function getCertificateByHash(string memory ipfsHash) public view returns (Certificate memory) {
        Certificate memory certificate = certificateByHash[ipfsHash];
        require(certificate.student != address(0), "Certificate not found");
        return certificate;
    }
}

